CREATE TABLE THONGTINLAPTOP
(	
	MALAPTOP VARCHAR(20) NOT NULL,
	TENLAPTOP VARCHAR(50),
	HANG VARCHAR(20),
	GIABAN BIGINT,
	SERIES VARCHAR(20),
	MAU VARCHAR(20),
	CPU	VARCHAR(50),
	VGA VARCHAR(50), 
	RAM CHAR(10),
	KICHTHUOCMANHINH VARCHAR(10),
	OCUNG VARCHAR(10),
	BANPHIM VARCHAR(10),
	PIN VARCHAR(50),
	KHOILUONG VARCHAR(10),
	LINKHINH1 VARCHAR(255),
	LINKHINH2 VARCHAR(255),
	LINKHINH3 VARCHAR(255),
	LINKHINH4 VARCHAR(255),
	LINKHINH5 VARCHAR(255)
);

CREATE TABLE HANGSX (
	TENHANG VARCHAR(20) NOT NULL PRIMARY KEY,
	QUOCGIA VARCHAR(50),
	POSTER VARCHAR(100),
	LOGOVUONG VARCHAR(100),
	LOGONGANG VARCHAR(100),
	SLOGAN VARCHAR(100)
);


CREATE TABLE GIOHANG (
	MAGIOHANG	VARCHAR(10) NOT NULL PRIMARY KEY,
	MAKH	INT NOT NULL,
	NGAYXUATGIOHANG VARCHAR(50),
	TRIGIA BIGINT
);


CREATE TABLE CTGH
(	
	MAGIOHANG VARCHAR(10) NOT NULL,
	MALAPTOP VARCHAR(20) NOT NULL,
	SOLUONG	INT
);



CREATE TABLE KHOHANG (
	MALAPTOP VARCHAR(20) NOT NULL PRIMARY KEY,
	SLNHAP	INT,
	SLXUAT INT,
	TONKHO INT
);



CREATE TABLE TK (
	ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
	HOTEN VARCHAR(50) NOT NULL,
	USERNAME VARCHAR(50) NOT NULL,
	PASSWORD VARCHAR(50) NOT NULL,
	DIACHI VARCHAR(50) NOT NULL,
	EMAIL VARCHAR(50) NOT NULL,
	OTP BIGINT NULL
);

CREATE TABLE PHANQUYEN (
	ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    QUYEN VARCHAR(10) NOT NULL
);
---------------------------------------------START SET KEY------------------------------------------------------
-- SET PRIMARY KEY
ALTER TABLE THONGTINLAPTOP ADD CONSTRAINT MALAPTOP_PK PRIMARY KEY (MALAPTOP);
ALTER TABLE GIOHANG ADD CONSTRAINT GIOHANG_PK PRIMARY KEY (MAGIOHANG);
ALTER TABLE HANGSX ADD CONSTRAINT TENHANG_PK PRIMARY KEY (TENHANG);
-- SET FOREIGN KEY
ALTER TABLE THONGTINLAPTOP ADD CONSTRAINT id5_FKKH FOREIGN KEY (HANG) REFERENCES HANGSX(TENHANG);
ALTER TABLE GIOHANG ADD CONSTRAINT id3_FKKH FOREIGN KEY (MAKH) REFERENCES TK(ID);
ALTER TABLE CTGH ADD CONSTRAINT id1_FKKH FOREIGN KEY (MAGIOHANG) REFERENCES GIOHANG(MAGIOHANG);
ALTER TABLE CTGH ADD CONSTRAINT id2_FKKH FOREIGN KEY (MALAPTOP) REFERENCES THONGTINLAPTOP(MALAPTOP);
ALTER TABLE KHOHANG ADD CONSTRAINT id4_FKKH FOREIGN KEY (MALAPTOP) REFERENCES THONGTINLAPTOP(MALAPTOP);
ALTER TABLE PHANQUYEN ADD CONSTRAINT id_FKKH FOREIGN KEY (ID) REFERENCES TK(ID);




